import logo from './logo.svg';
import './App.css';
import Nofication from './components/Notification';
import BudgetForm from './components/BudgetForm';
import BudgetList from './components/BudgetList';
import uuid from 'uuid/v4'

const initialExpenses = [
  { id: uuid(), charge: 'rent', amount: 1000 },
  { id: uuid(), charge: 'skySurf', amount: 1300 },
  { id: uuid(), charge: 'diving', amount: 1000 }
]



function App()
{
  //useState is a function that take an intialValue and return an array with 2 values
  //the action value of the state
  //function to update/controle
  //default valie
  const [expenses, setExpenses] = useState(initialExpenses)
  const [charge, setCharge] = useState('')
  const [amount, setAmount] = useState('')



