    {

    }
  }

  return (
    <>
      <Nofication />
      <h1>Budget App</h1>
      <main className="App">
        <BudgetForm
          amount={amount}
          charge={charge}
          handleAmount={handleAmount}
          handleCharge={handleCharge}
          handleSubmit={handleSubmit}
           />
        <BudgetList expenses={expenses} />
      </main>
      <h1>
        Total spending :{" "}
        <span className="total">
          ${expenses.reduce((acc, curr) =>
          {
            return acc += curr.amount
          },0)}
        </span>
      </h1>
    </>
  );
}

export default App;
